import Choices from 'choices.js';
import { Controller } from '@hotwired/stimulus';
import Rails from '@rails/ujs';

import 'choices.js/public/assets/styles/choices.min.css';

export default class extends Controller {
  static targets = ['select', 'listData'];

  // constructor(context) {
  //   super(context);
  //   this.instances = {};
  // }
  connect() {}
  selectTargetConnected(currentSelectTarget, search1) {
    const instanceId = this.generateInstanceId(currentSelectTarget);
    this.instances[instanceId] = new Choices(currentSelectTarget, {
      removeItemButton: true,
      itemSelectText: '',
      removeItems: false,
      position: 'bottom',
    });
    const baseUrl = this.selectTarget.dataset.url;
    Rails.ajax({
      url: `${baseUrl}?depart=${currentSelectTarget.value}?search=${2}`,
      type: 'get',
      dataType: 'json',
      success: (res) => {
        this.listDataTarget.innerHTML = res.html;
      },
    });
  }

  // selectTargetDisconnected(currentSelectTarget) {
