import Choices from 'choices.js';
import { Controller } from '@hotwired/stimulus';
import Rails from '@rails/ujs';

import 'choices.js/public/assets/styles/choices.min.css';

export default class extends Controller {
  static targets = ['select', 'input', 'listData'];

  // constructor(context) {
  //   super(context);
  //   this.instances = {};
  // }
  connect() {}
  selectTargetConnected(currentSelectTarget) {
    const instanceId = this.generateInstanceId(currentSelectTarget);
    this.instances = new Choices(currentSelectTarget, {
      removeItemButton: true,
      itemSelectText: '',
      removeItems: false,
      position: 'bottom',
    });
    // const baseUrl = '/admin/employees';
    // // debugger;
    // Rails.ajax({
    //   url: `${baseUrl}?search=${this.inputTarget.value}&depart=${this.selectTarget.value}`,
    //   type: 'get',
    //   dataType: 'json',
    //   success: (res) => {
    //     this.listDataTarget.innerHTML = res.html;
    //   },
    // });
    // debugger;
  }

  selectTargetDisconnected(currentSelectTarget) {
    const instanceId = this.getInstanceId(currentSelectTarget);
    debugger;
    this.instances[instanceId]?.destroy();
    delete this.instances[instanceId];
  }

  generateInstanceId(element) {
    const uniqeId = `${Date.now().toString(36)}-${Math.random()
      .toString(36)
      .slice(2)}`;
    // eslint-disable-next-line no-param-reassign
    element.dataset.choicesId = uniqeId;
    return uniqeId;
  }

  getInstanceId(element) {
    return element.dataset.choicesId;
  }
}
